"use strict";
// tipo array: defini-se antes do [] qual o tipo de elementos que estaão no array
let array;
array = [1, 2, 3];
array = ['1', 'Fabiano'];
// outra forma de se defir o array:
let array2;
array2 = [1, 2, 3];
array2 = ['1', 'Fabiano'];
// as tuplas também sao arrays porém com um formato definido, isto é, o número de elementos e o tipo de cada elemento são conhecidos  
let tuple;
tuple = [2, 'Fabiano'];
tuple = [2, 4];
tuple = [2, 'Fabiano', 4];
